import React, { useState, useEffect } from 'react';
import '../style/receipt.scss';
import Draggable from 'react-draggable';

const Receipt = ({ receiptItems, onAddMenuItem, onClose, title }) => {
  const [total, setTotal] = useState(0);

  useEffect(() => {
    calculateTotal();
  }, [receiptItems]);

  const calculateTotal = () => {
    let newTotal = receiptItems.reduce((acc, item) => acc + item.price * item.quantity, 0);
    setTotal(newTotal);
  };

  return (
    <Draggable>
      <div className="receipt-container">
        <button className="close-button" onClick={onClose}>Ã—</button>
        <div className="receipt-header">
          <h2>{title}</h2>
          <div className="receipt-meta">
            <p>served by</p>
            <input type="text" placeholder="Server Name" />
            <p>{new Date().toLocaleString()}</p>
          </div>
        </div>
        <table className="receipt-table">
          <thead>
            <tr>
              <th>QTY</th>
              <th>ITEM</th>
              <th>PRICE</th>
            </tr>
          </thead>
          <tbody>
            {receiptItems.map((item, index) => (
              <tr key={index}>
                <td>{item.quantity}</td>
                <td>{item.name}</td>
                <td>${(item.price * item.quantity).toFixed(2)}</td>
              </tr>
            ))}
          </tbody>
        </table>
        <div className="receipt-total">
          <p>TOTAL: ${total.toFixed(2)}</p>
        </div>
        <div className="receipt-actions">
          <button className="receipt-button">discount</button>
          <button className="receipt-button">split</button>
          <button className="receipt-button">Pay ($)</button>
        </div>
      </div>
    </Draggable>
  );
};

export default Receipt;
