const AddButton = styled.button`
  width: 100%;
  padding: 1rem;
  border: 1px solid #0ACDFF;
  background-color: white;
  color: #0ACDFF;
  border-radius: 0.25rem;
  cursor: pointer;
  margin-top: 1rem;

  &:hover {
    background-color: #0ACDFF;
    color: white;
  }
`;

const Menu = () => {
  const [menuItems, setMenuItems] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [isCreatingItem, setIsCreatingItem] = useState(false);
  const [newItem, setNewItem] = useState({ name: '', price: '', description: '', image: '', options: [] });
  const [optionName, setOptionName] = useState('');
  const [optionPrice, setOptionPrice] = useState('');
  const [selectedItem, setSelectedItem] = useState(null);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewItem({ ...newItem, [name]: value });
  };

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    const reader = new FileReader();
    reader.onloadend = () => {
      setNewItem({ ...newItem, image: reader.result });
    };
    if (file) {
      reader.readAsDataURL(file);
    }
  };

  const handleSaveItem = () => {
    setMenuItems([...menuItems, newItem]);
    setNewItem({ name: '', price: '', description: '', image: '', options: [] });
    setIsCreatingItem(false);
  };

  const handleAddOption = () => {
    const newOption = { name: optionName, price: optionPrice };
    setNewItem({ ...newItem, options: [...newItem.options, newOption] });
    setOptionName('');
    setOptionPrice('');
  };

  const handleRemoveOption = (index) => {
    const updatedOptions = newItem.options.filter((_, i) => i !== index);
    setNewItem({ ...newItem, options: updatedOptions });
  };

  const filteredMenuItems = menuItems.filter(item =>
    item.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const handleItemClick = (item) => {
    setSelectedItem(item);
  };

  const handleClosePanel = () => {
    setSelectedItem(null);
  };

  const calculateTotal = (price, options) => {
    const subtotal = options.reduce((acc, option) => acc + parseFloat(option.price || 0), parseFloat(price));
    const tax = subtotal * 0.13;
    const total = subtotal + tax;
    return { subtotal, tax, total };
  };

  return (
    <MenuContainer>
      {!isCreatingItem ? (
        <>
          <MenuOptions>
            <SearchBar
              type="text"
              placeholder="Search"
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
            <div>
              <MenuButton onClick={() => setIsCreatingItem(true)}>Create Menu Item</MenuButton>
            </div>
          </MenuOptions>
          <MenuList>
            {filteredMenuItems.length > 0 ? (
              filteredMenuItems.map((item, index) => (
                <MenuItem key={index} onClick={() => handleItemClick(item)}>
                  {item.image && <img src={item.image} alt={item.name} />}
                  <h4>{item.name}</h4>
                  <p>{item.price}</p>
                </MenuItem>
              ))
            ) : (
              <p>+ Add menu items</p>
            )}
          </MenuList>
        </>
      ) : (
        <FormContainer>
          <h2>Create Menu Item</h2>
          <Label>Name</Label>
          <InputField
            type="text"
            name="name"
            value={newItem.name}
            onChange={handleInputChange}
          />
          <Label>Price</Label>
          <InputField
            type="text"
            name="price"
            value={newItem.price}
            onChange={handleInputChange}
          />
          <Label>Description</Label>
          <TextArea
            name="description"
            value={newItem.description}
            onChange={handleInputChange}
          />
          <Label>Image</Label>
          <ImageUpload>
            {newItem.image ? (
              <img src={newItem.image} alt="Menu item" />
            ) : (
              <>
                <input
                  type="file"
                  id="imageUpload"
                  onChange={handleImageUpload}
                />
                <label htmlFor="imageUpload">Drag and drop to upload file or <span>Browse</span></label>
              </>
            )}
          </ImageUpload>
          <Label>Options</Label>
          <OptionsContainer>
            <OptionList>
              {newItem.options.map((option, index) => (
                <div key={index} className="option">
                  {option.name} +${option.price}
                  <button className="remove-button" onClick={() => handleRemoveOption(index)}>x</button>
                </div>
              ))}
            </OptionList>
            <OptionInputContainer>
              <InputField
                type="text"
                placeholder="Name"
                value={optionName}
                onChange={(e) => setOptionName(e.target.value)}
              />
              <InputField
                type="text"
                placeholder="Price"
                value={optionPrice}
                onChange={(e) => setOptionPrice(e.target.value)}
              />
              <button
                className="add-option-button"
                onClick={handleAddOption}
              >
                ✓
              </button>
            </OptionInputContainer>
          </OptionsContainer>
          <ButtonContainer>
            <MenuButton onClick={() => setIsCreatingItem(false)}>Close</MenuButton>
            <MenuButton onClick={handleSaveItem}>Save</MenuButton>
          </ButtonContainer>
        </FormContainer>
      )}

      <SlideOutPanel isOpen={selectedItem !== null} className="menu-slide-out-panel">
        {selectedItem && (
          <>
            <CloseButton onClick={handleClosePanel}>×</CloseButton>
            <h2 className="slide-out-title" style={{fontSize: '2rem', textTransform: 'capitalize', color: '#004E7A'}}>{selectedItem.name}</h2>
            {selectedItem.image && <img className="slide-out-image" style={{width: '100%'}} src={selectedItem.image} alt={selectedItem.name} />}
            <p><strong >Price:</strong> {selectedItem.price}</p>
            <p><strong>Description:</strong> {selectedItem.description}</p>
            <p><strong>Options:</strong></p>
            <ul>
              {selectedItem.options.map((option, index) => (
                <li key={index}>{option.name} +${option.price}</li>
              ))}
            </ul>
            <div style={{ border: '1px solid grey', width: '90%', position: 'absolute', bottom: '1rem'}}>
                <p><strong>Subtotal:</strong> ${calculateTotal(selectedItem.price, selectedItem.options).subtotal.toFixed(2)}</p>
                <p><strong>Tax:</strong> ${calculateTotal(selectedItem.price, selectedItem.options).tax.toFixed(2)}</p>
                <p><strong>Total:</strong> ${calculateTotal(selectedItem.price, selectedItem.options).total.toFixed(2)}</p>
                <AddButton>+ Add to Table</AddButton>
                <AddButton>+ Add to Takeout</AddButton>
            </div>
          </>
        )}
      </SlideOutPanel>
    </MenuContainer>
  );
};

export default Menu;
