import React, { useState } from 'react';
import Modal from 'react-modal';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import '../style/splitBillModal.scss';

const SplitBillModal = ({ isOpen, onRequestClose, receiptItems, onSplitReceiptsUpdate }) => {
  const [splitReceipts, setSplitReceipts] = useState([{ id: 'receipt-1', items: [] }]);

  const addNewReceipt = () => {
    setSplitReceipts([...splitReceipts, { id: `receipt-${splitReceipts.length + 1}`, items: [] }]);
  };

  const onDragEnd = (result) => {
    if (!result.destination) {
      return;
    }

    const sourceIndex = result.source.index;
    const destinationIndex = result.destination.index;

    const sourceReceiptIndex = splitReceipts.findIndex((receipt) => receipt.id === result.source.droppableId);
    const destinationReceiptIndex = splitReceipts.findIndex((receipt) => receipt.id === result.destination.droppableId);

    const sourceItems = Array.from(splitReceipts[sourceReceiptIndex].items);
    const [removedItem] = sourceItems.splice(sourceIndex, 1);

    const destinationItems = Array.from(splitReceipts[destinationReceiptIndex].items);
    destinationItems.splice(destinationIndex, 0, removedItem);

    const newReceipts = [...splitReceipts];
    newReceipts[sourceReceiptIndex].items = sourceItems;
    newReceipts[destinationReceiptIndex].items = destinationItems;

    setSplitReceipts(newReceipts);
    onSplitReceiptsUpdate(newReceipts);
  };

  return (
    <Modal isOpen={isOpen} onRequestClose={onRequestClose} contentLabel="Split Bill Modal" className="split-bill-modal">
      <h2>Split Bill</h2>
      <div className="split-bill-container">
        <DragDropContext onDragEnd={onDragEnd}>
          <Droppable droppableId="receipt-items" type="ITEM">
            {(provided) => (
              <div className="receipt-items" {...provided.droppableProps} ref={provided.innerRef}>
                {receiptItems.map((item, index) => (
                  <Draggable key={item.id} draggableId={item.id} index={index}>
                    {(provided) => (
                      <div
                        className="receipt-item"
                        ref={provided.innerRef}
                        {...provided.draggableProps}
                        {...provided.dragHandleProps}
                      >
                        {item.name} - ${item.price.toFixed(2)}
                      </div>
                    )}
                  </Draggable>
                ))}
                {provided.placeholder}
              </div>
            )}
          </Droppable>
          <div className="split-receipts">
            {splitReceipts.map((receipt) => (
              <Droppable key={receipt.id} droppableId={receipt.id} type="ITEM">
                {(provided) => (
                  <div className="split-receipt" {...provided.droppableProps} ref={provided.innerRef}>
                    <h3>{receipt.id}</h3>
                    {receipt.items.map((item, index) => (
                      <Draggable key={item.id} draggableId={item.id} index={index}>
                        {(provided) => (
                          <div
                            className="receipt-item"
                            ref={provided.innerRef}
                            {...provided.draggableProps}
                            {...provided.dragHandleProps}
                          >
                            {item.name} - ${item.price.toFixed(2)}
                          </div>
                        )}
                      </Draggable>
                    ))}
                    {provided.placeholder}
                  </div>
                )}
              </Droppable>
            ))}
          </div>
        </DragDropContext>
      </div>
      <button onClick={addNewReceipt} className="add-receipt-button">+ Add Bill</button>
    </Modal>
  );
};

export default SplitBillModal;
