import React, { useState } from 'react';
import '../style/menu.scss';
import Receipt from './Receipt';

const Menu = () => {
  const [menuItems, setMenuItems] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [isCreatingItem, setIsCreatingItem] = useState(false);
  const [newItem, setNewItem] = useState({ name: '', price: '', cost: '', description: '', image: '', options: [], isInventory: false, quantity: '' });
  const [optionName, setOptionName] = useState('');
  const [optionPrice, setOptionPrice] = useState('');
  const [selectedItem, setSelectedItem] = useState(null);
  const [selectedOptions, setSelectedOptions] = useState([]);
  const [receiptItems, setReceiptItems] = useState([]);
  const [showReceipt, setShowReceipt] = useState(false);
  const [receiptTitle, setReceiptTitle] = useState('Receipt for Table A20');

  const handleInputChange = (e) => {
    const { name, value } = e.target;

    // Only allow numbers and a single decimal point for the 'cost' field
    if (name === "cost" && !/^\d*\.?\d*$/.test(value)) {
      return; // Prevent updating if the value is not a valid number
    }

    setNewItem({ ...newItem, [name]: value });
  };

  const handleCheckboxChange = () => {
    setNewItem({ ...newItem, isInventory: !newItem.isInventory, quantity: '' });
  };

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    const reader = new FileReader();
    reader.onloadend = () => {
      setNewItem({ ...newItem, image: reader.result });
    };
    if (file) {
      reader.readAsDataURL(file);
    }
  };

  const handleSaveItem = () => {
    setMenuItems([...menuItems, newItem]);
    setNewItem({ name: '', price: '', cost: '', description: '', image: '', options: [], isInventory: false, quantity: '' });
    setIsCreatingItem(false);
  };

  const handleAddOption = () => {
    const newOption = { name: optionName, price: optionPrice };
    setNewItem({ ...newItem, options: [...newItem.options, newOption] });
    setOptionName('');
    setOptionPrice('');
  };

  const handleRemoveOption = (index) => {
    const updatedOptions = newItem.options.filter((_, i) => i !== index);
    setNewItem({ ...newItem, options: updatedOptions });
  };

  return (
    <div className="menu-container">
      {!isCreatingItem ? (
        <>
          <div className="menu-options">
            <input
              type="text"
              className="search-bar"
              placeholder="Search"
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
            <button className="menu-filter-button">
              <img src="/assets/icons/filter-icon.svg"/>
            </button>
            <div>
              <button className="menu-button" onClick={() => setIsCreatingItem(true)}>+ Create Menu Item</button>
            </div>
            <div>
              <button className="menu-button">+ Create Menu Page</button>
            </div>
          </div>
          <div className="menu-list">
            {menuItems.map((item, index) => (
              <div key={index} className="menu-item" onClick={() => setSelectedItem(item)}>
                {item.image && <img src={item.image} alt={item.name} />}
                <h4 className="menu-item-name">{item.name}</h4>
                <p className="menu-item-price">${item.price}</p>
              </div>
            ))}
          </div>
        </>
      ) : (
        <div className="form-container">
          <h2>Create Menu Item</h2>
          <label className="label">Inventory Item</label>
          <input
            type="checkbox"
            className="input-field"
            checked={newItem.isInventory}
            onChange={handleCheckboxChange}
          />
          {newItem.isInventory && (
            <>
              <label className="label">Quantity</label>
              <input
                type="number"
                className="input-field"
                name="quantity"
                value={newItem.quantity}
                onChange={handleInputChange}
              />
            </>
          )}
          <label className="label">Name</label>
          <input
            type="text"
            className="input-field"
            name="name"
            value={newItem.name}
            onChange={handleInputChange}
          />
          <label className="label">Price</label>
          <input
            type="text"
            className="input-field"
            name="price"
            value={newItem.price}
            onChange={handleInputChange}
          />
          <label className="label">Cost</label>
          <input
            type="text"
            className="input-field"
            name="cost"
            value={newItem.cost}
            onChange={handleInputChange}
          />
          <label className="label">Description</label>
          <textarea
            className="text-area"
            name="description"
            value={newItem.description}
            onChange={handleInputChange}
          />
          <label className="label">Image</label>
          <div className="image-upload">
            {newItem.image ? (
              <img src={newItem.image} alt="Menu item" />
            ) : (
              <>
                <input
                  type="file"
                  id="imageUpload"
                  onChange={handleImageUpload}
                />
                <label htmlFor="imageUpload">Drag and drop to upload file or <span>Browse</span></label>
              </>
            )}
          </div>
          <label className="label">Options</label>
          <div className="options-container">
            <div className="option-list">
              {newItem.options.map((option, index) => (
                <div key={index} className="option">
                  {option.name} +${option.price}
                  <button className="remove-button" onClick={() => handleRemoveOption(index)}>x</button>
                </div>
              ))}
            </div>
            <div className="option-input-container">
              <input
                type="text"
                placeholder="Name"
                value={optionName}
                onChange={(e) => setOptionName(e.target.value)}
              />
              <input
                type="text"
                placeholder="Price"
                value={optionPrice}
                onChange={(e) => setOptionPrice(e.target.value)}
              />
              <button
                className="add-option-button"
                onClick={handleAddOption}
              >
                âœ“
              </button>
            </div>
          </div>
          <div className="button-container">
            <button className="menu-button" onClick={() => setIsCreatingItem(false)}>Close</button>
            <button className="menu-button" onClick={handleSaveItem}>Save</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Menu;
