import React, { useState } from 'react';
import styled from 'styled-components';

const MenuContainer = styled.div`
  padding: 2rem;
`;

const SearchBar = styled.input`
  width: 100%;
  padding: 0.5rem;
  margin-bottom: 1rem;
  border: 1px solid #ccc;
  border-radius: 0.25rem;
`;

const MenuOptions = styled.div`
  display: flex;
  justify-content: space-between;
  margin-bottom: 1rem;
`;

const MenuButton = styled.button`
  padding: 0.5rem 1rem;
  border: 1px solid #0ACDFF;
  background-color: white;
  color: #0ACDFF;
  border-radius: 0.25rem;
  cursor: pointer;

  &:hover {
    background-color: #0ACDFF;
    color: white;
  }
`;

const MenuList = styled.div`
  display: flex;
  flex-wrap: wrap;
`;

const MenuItem = styled.div`
  width: 200px;
  margin: 1rem;
  padding: 1rem;
  border: 1px solid #ccc;
  border-radius: 0.25rem;
  text-align: center;
`;

const FormContainer = styled.div`
  display: flex;
  flex-direction: column;
  max-width: 600px;
  margin: 0 auto;
`;

const InputField = styled.input`
  padding: 0.5rem;
  margin: 0.5rem 0;
  border: 1px solid #ccc;
  border-radius: 0.25rem;
`;

const TextArea = styled.textarea`
  padding: 0.5rem;
  margin: 0.5rem 0;
  border: 1px solid #ccc;
  border-radius: 0.25rem;
`;

const ButtonContainer = styled.div`
  display: flex;
  justify-content: flex-end;
  margin-top: 1rem;
`;

const Menu = () => {
  const [menuItems, setMenuItems] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [isCreatingItem, setIsCreatingItem] = useState(false);
  const [newItem, setNewItem] = useState({ name: '', price: '', description: '', image: '' });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewItem({ ...newItem, [name]: value });
  };

  const handleSaveItem = () => {
    setMenuItems([...menuItems, newItem]);
    setNewItem({ name: '', price: '', description: '', image: '' });
    setIsCreatingItem(false);
  };

  const filteredMenuItems = menuItems.filter(item =>
    item.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <MenuContainer>
      {!isCreatingItem ? (
        <>
          <MenuOptions>
            <SearchBar
              type="text"
              placeholder="Search"
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
            <div>
              <MenuButton onClick={() => setIsCreatingItem(true)}>Create Menu Item</MenuButton>
            </div>
          </MenuOptions>
          <MenuList>
            {filteredMenuItems.length > 0 ? (
              filteredMenuItems.map((item, index) => (
                <MenuItem key={index}>
                  <h4>{item.name}</h4>
                  <p>{item.price}</p>
                </MenuItem>
              ))
            ) : (
              <p>+ Add menu items</p>
            )}
          </MenuList>
        </>
      ) : (
        <FormContainer>
          <h2>Create Menu Item</h2>
          <label>Name</label>
          <InputField
            type="text"
            name="name"
            value={newItem.name}
            onChange={handleInputChange}
          />
          <label>Price</label>
          <InputField
            type="text"
            name="price"
            value={newItem.price}
            onChange={handleInputChange}
          />
          <label>Description</label>
          <TextArea
            name="description"
            value={newItem.description}
            onChange={handleInputChange}
          />
          <label>Image</label>
          <InputField
            type="text"
            name="image"
            value={newItem.image}
            onChange={handleInputChange}
          />
          <ButtonContainer>
            <MenuButton onClick={() => setIsCreatingItem(false)}>Close</MenuButton>
            <MenuButton onClick={handleSaveItem}>Save</MenuButton>
          </ButtonContainer>
        </FormContainer>
      )}
    </MenuContainer>
  );
};

export default Menu;
