import React, { useState } from 'react';
import '../style/receipt.scss';

const Receipt = ({ menuItem, onAddMenuItem }) => {
  const [items, setItems] = useState([{ ...menuItem, qty: 1 }]);

  const addItem = () => {
    onAddMenuItem();
  };

  const updateItemQty = (index, qty) => {
    const updatedItems = [...items];
    updatedItems[index].qty = qty;
    setItems(updatedItems);
  };

  const calculateTotal = () => {
    return items.reduce((total, item) => total + item.price * item.qty, 0).toFixed(2);
  };

  return (
    <div className="receipt">
      <div className="receipt-header">
        <h2>Receipt for table A20</h2>
        <div className="served-by">
          <label>served by</label>
          <input type="text" placeholder="Server Name" />
        </div>
        <div className="date-time">{new Date().toLocaleString()}</div>
      </div>
      <table className="receipt-table">
        <thead>
          <tr>
            <th>QTY</th>
            <th>ITEM</th>
            <th>PRICE</th>
          </tr>
        </thead>
        <tbody>
          {items.map((item, index) => (
            <tr key={index}>
              <td>
                <input
                  type="number"
                  value={item.qty}
                  onChange={(e) => updateItemQty(index, parseInt(e.target.value))}
                />
              </td>
              <td>{item.name}</td>
              <td>${(item.price * item.qty).toFixed(2)}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <button className="add-item-button" onClick={addItem}>
        + Add menu item
      </button>
      <div className="receipt-total">
        <strong>TOTAL: ${calculateTotal()}</strong>
      </div>
      <div className="receipt-actions">
        <button className="action-button">discount</button>
        <button className="action-button">split</button>
        <button className="action-button">Pay ($)</button>
      </div>
      {/* <img className="receipt-background" src="/assets/receipt.svg"/> */}
    </div>
  );
};

export default Receipt;
