import React, { useState, useRef, useEffect } from "react";
import styled from "styled-components";
import DraggableTable from "./DraggableTable";

const Grid = styled.div`
  width: 100%;
  height: 100%;
  position: absolute;
`;

export default function TableManagement() {
  const [zoomLevel, setZoomLevel] = useState(100);
  const [gridElements, setGridElements] = useState([]);
  const [isDragging, setIsDragging] = useState(false);
  const [isSpacePressed, setIsSpacePressed] = useState(false);
  const [gridPosition, setGridPosition] = useState({ x: 0, y: 0 });
  const gridRef = useRef(null);
  const containerRef = useRef(null);

  const handleZoomLevel = (value) => {
    console.log(value);
    if (zoomLevel + value > 50 && zoomLevel + value < 150) {
      setZoomLevel(zoomLevel + value);
    }
  };

  const addElementToGrid = (element, position) => {
    setGridElements((prevElements) => [
      ...prevElements,
      { element, position },
    ]);
  };

  const updateElementPosition = (index, position) => {
    setGridElements((prevElements) => {
      const newElements = [...prevElements];
      newElements[index] = {
        ...newElements[index],
        position,
      };
      return newElements;
    });
  };

  const handleMouseDown = (e) => {
    if (isSpacePressed) {
      setIsDragging(true);
      const startX = e.clientX - gridPosition.x;
      const startY = e.clientY - gridPosition.y;
      containerRef.current = { startX, startY };
    }
  };

  const handleMouseMove = (e) => {
    if (isDragging && containerRef.current) {
      const x = e.clientX - containerRef.current.startX;
      const y = e.clientY - containerRef.current.startY;
      setGridPosition({ x, y });
    }
  };

  const handleMouseUp = () => {
    setIsDragging(false);
  };

  const handleKeyDown = (e) => {
    if (e.key === " ") {
      e.preventDefault(); // Prevent the default scrolling behavior
      setIsSpacePressed(true);
    }
  };


  const handleKeyUp = (e) => {
    if (e.key === " ") {
      setIsSpacePressed(false);
    }
  };

  useEffect(() => {
    document.addEventListener("keydown", handleKeyDown);
    document.addEventListener("keyup", handleKeyUp);
    document.addEventListener("mousemove", handleMouseMove);
    document.addEventListener("mouseup", handleMouseUp);
    return () => {
      document.removeEventListener("keydown", handleKeyDown);
      document.removeEventListener("keyup", handleKeyUp);
      document.removeEventListener("mousemove", handleMouseMove);
      document.removeEventListener("mouseup", handleMouseUp);
    };
  }, [isDragging]);


  return (
    <div className="table-management">
      <div>
      <div className={`table-grid-container ${isDragging ? 'grid-animated' : 'grid-normal'}`}
        onMouseDown={handleMouseDown}>
        <div className="table-zoom-settings">
          <button onClick={() => handleZoomLevel(10)} className="zoom-button">+</button>
          <button onClick={() => handleZoomLevel(-10)} className="zoom-button">-</button>
          <button onClick={() => setZoomLevel(1)} className="zoom-button">{zoomLevel}%</button>
        </div>
        <Grid 
          style={{
            transform: `scale(${zoomLevel / 100}) translate(${gridPosition.x}px, ${gridPosition.y}px)`,
          }}
          ref={gridRef} 
          className="table-grid" 
        >
          {gridElements.map((item, index) => (
              <DraggableTable
                key={index}
                img={item.element.props.img}
                gridRef={gridRef}
                initialPosition={item.position}
                isInGrid={true}
                updatePosition={(position) => updateElementPosition(index, position)}
              />
          ))}
        </Grid>
      </div>
      </div>
      <div className="table-presets">
        <h3 className="edit-tables-title">Edit Tables</h3>
        <div className="table-options">
          <div className="table-option"               
     >
            <DraggableTable
              img="/assets/icons/tables/round-table.svg"
              gridRef={gridRef}
              addElementToGrid={addElementToGrid}
              resetOnDrop
            />
          </div>
          <div className="table-option">
            <DraggableTable
              img="/assets/icons/tables/square-table.svg"
              gridRef={gridRef}
              addElementToGrid={addElementToGrid}
              resetOnDrop
            />
          </div>
          <div className="table-option">
            <DraggableTable
              img="/assets/icons/tables/wide-rectangle-table.svg"
              gridRef={gridRef}
              addElementToGrid={addElementToGrid}
              resetOnDrop
            />
          </div>
          <div className="table-option">
            <DraggableTable
              img="/assets/icons/tables/long-rectangle-table.svg"
              gridRef={gridRef}
              addElementToGrid={addElementToGrid}
              resetOnDrop
            />
          </div>
        </div>
        <div className="saved-presets">
            You currently have no saved presets. Presets wil show up
            here when you create some.
        </div>
        <div className="preset-buttons">
          <button className="preset-button">Save Preset</button>
          <button className="preset-button">Delete Preset</button>
        </div>
      </div>
    </div>
  );
}
