import React, { useState, useRef, useEffect } from "react";
import styled from "styled-components";
import DraggableTable from "./DraggableTable";

const Grid = styled.div`
  width: 100%;
  height: 100%;
  position: absolute;
`;

export default function TableManagement() {
  const [zoomLevel, setZoomLevel] = useState(100);
  const [gridElements, setGridElements] = useState([]);
  const [isDragging, setIsDragging] = useState(false);
  const [isSpacePressed, setIsSpacePressed] = useState(false);
  const [gridPosition, setGridPosition] = useState({ x: 0, y: 0 });
  const gridRef = useRef(null);
  const containerRef = useRef(null);

  const handleZoomLevel = (value) => {
    console.log(value);
    if (zoomLevel + value > 50 && zoomLevel + value < 150)
      setZoomLevel(zoomLevel + value);
  };

  const addElementToGrid = (element, position) => {
    setGridElements((prevElements) => [
      ...prevElements,
      { element, position },
    ]);
  };

  const updateElementPosition = (index, position) => {
    setGridElements((prevElements) => {
      const newElements = [...prevElements];
      newElements[index] = {
        ...newElements[index],
        position,
      };
      return newElements;
    });
  };

  const handleMouseDown = (e) => {
    if (isSpacePressed) {
      setIsDragging(true);
      containerRef.current.startX = e.clientX - gridPosition.x;
      containerRef.current.startY = e.clientY - gridPosition.y;
    }
  };

  const handleMouseMove = (e) => {
    if (isDragging) {
      const x = e.clientX - containerRef.current.startX;
      const y = e.clientY - containerRef.current.startY;
      setGridPosition({ x, y });
    }
  };

  const handleMouseUp = () => {
    setIsDragging(false);
  };

  const handleKeyDown = (e) => {
    if (e.key === " ") {
      setIsSpacePressed(true);
    }
  };

  const handleKeyUp = (e) => {
    if (e.key === " ") {
      setIsSpacePressed(false);
    }
  };

  useEffect(() => {
    document.addEventListener("keydown", handleKeyDown);
    document.addEventListener("keyup", handleKeyUp);
    document.addEventListener("mousemove", handleMouseMove);
    document.addEventListener("mouseup", handleMouseUp);
    return () => {
      document.removeEventListener("keydown", handleKeyDown);
      document.removeEventListener("keyup", handleKeyUp);
      document.removeEventListener("mousemove", handleMouseMove);
      document.removeEventListener("mouseup", handleMouseUp);
    };
  }, [isDragging]);

  return (
    <div className="table-management" ref={containerRef}>
      <div
        className={`table-grid-container ${isDragging ? "grid-animated" : "grid-normal"}`}
        onMouseDown={handleMouseDown}
      >
        <div className="table-zoom-settings">
          <button onClick={() => handleZoomLevel(10)} className="zoom-button">
            +
          </button>
          <button onClick={() => handleZoomLevel(-10)} className="zoom-button">
            -
          </button>
          <button onClick={() => setZoomLevel(1)} className="zoom-button">
            {zoomLevel}%
          </button>
        </div>
        <Grid
          style={{
            transform: `scale(${zoomLevel / 100}) translate(${gridPosition.x}px, ${gridPosition.y}px)`,
          }}
          ref={gridRef}
          className="table-grid"
        >
          {gridElements.map((item, index) => (
            <DraggableTable
              key={index}
              img={item.element.props.img}
              gridRef={gridRef}
              initialPosition={item.position}
              isInGrid={true}
              updatePosition={(position) => updateElementPosition(index, position)}
            />
          ))}
        </Grid>
      </div>
      <div className="table-presets">
        <h3 className="edit-tables-title">Edit Tables</h3>
        <div className="table-options">
        <div className="table-option"               
            onMouseDown={()=>{
                  console.log("DRAG")
                  setIsDragging(true)
              }}
            onMouseUp={()=>setIsDragging(false)}>

          <DraggableTable
            img="/assets/icons/tables/round-table.svg"
            gridRef={gridRef}
            addElementToGrid={addElementToGrid}
            resetOnDrop
          />
          </div>
          <DraggableTable
            img="/assets/icons/tables/square-table.svg"
            gridRef={gridRef}
            addElementToGrid={addElementToGrid}
            resetOnDrop
          />
          <DraggableTable
            img="/assets/icons/tables/wide-rectangle-table.svg"
            gridRef={gridRef}
            addElementToGrid={addElementToGrid}
            resetOnDrop
          />
          <DraggableTable
            img="/assets/icons/tables/long-rectangle-table.svg"
            gridRef={gridRef}
            addElementToGrid={addElementToGrid}
            resetOnDrop
          />
        </div>
        <div className="saved-presets"></div>
        <div className="preset-buttons">
          <button>Save Preset</button>
          <button>Delete Preset</button>
        </div>
      </div>
    </div>
  );
}
