import './App.scss';
import Header from './components/Header';
import Nav from './components/Nav';
import Icon from './components/Icon';
import TableManagement from './components/TableManagement';
import Menu from './components/Menu'; // Import the new Menu component
import { useState } from 'react';

import './style/main.scss';

function App() {
  const [activeTab, setActiveTab] = useState('tables'); // State for managing active tab

  return (
    <div className="App">
      <Nav />
      <section>
        <Header 
          title="Mahad's Cuisine & Bar"
        />
        <div className="tabs">
          <button className="tab-button" onClick={() => setActiveTab('tables')}>
            <Icon 
              img="/assets/icons/tables/round-table.svg"
              width="2rem"
              padding="0"
            />
            Tables
          </button>
          <button className="tab-button" onClick={() => setActiveTab('reservations')}>
            <Icon 
              img="/assets/icons/reservation-icon.svg"
              width="2rem"
              padding="0"
            />
            Reservations
          </button>
          <button className="tab-button" onClick={() => setActiveTab('menu')}>
            <Icon 
              img="/assets/icons/hotdish-icon-blue.svg"
              width="2rem"
              padding="0"
            />
            Menu
          </button>
        </div>

        {activeTab === 'tables' && <TableManagement />}
        {activeTab === 'reservations' && <div>Reservations Content</div>}
        {activeTab === 'menu' && <Menu />} {/* Render the Menu component */}
      </section>
    </div>
  );
}

export default App;
