import React from "react";
import { useState } from "react";
import { styled } from "styled-components";

import Icon from "../components/Icon";



export default function TableManagement(){
    const [zoomLevel, setZoomLevel] = useState(1);

    const Grid = styled.div` 
        background-size: ${40 + 1*zoomLevel}px ${40 + 1*zoomLevel}px;   
    `

    const TableImg = styled.img` 
        width: 60%;
        height: 60%;
    `

    const handleZoomLevel = value => {
        console.log(zoomLevel);
        setZoomLevel(zoomLevel + value);
    };

    const handleDrag = e => {
        console.log(e);
        e = e || window.event;
    }

    return(
        <div className="table-management">
            <div className="table-grid-container">
                <div className="table-zoom-settings">
                    <button onClick={()=>handleZoomLevel(10)}>+</button>
                    <button onClick={()=>handleZoomLevel(-10)}>-</button>
                    <button>100%</button>
                </div>
                <Grid className="table-grid">

                </Grid>
            </div>
            <div className="table-presets">
                <h3 className="edit-tables-title">Edit Tables</h3>
                <div className="table-options">
                    <div className="table-option" onMouseDown={()=>handleDrag(this)}>
                        <img className="draggable-icon" src="/assets/icons/draggable-icon.svg"/>
                        <TableImg src="/assets/icons/tables/round-table.svg"/>
                    </div>
                    <div className="table-option">
                        <img className="draggable-icon" src="/assets/icons/draggable-icon.svg"/>
                        <TableImg src="/assets/icons/tables/square-table.svg"/>
                    </div>
                    <div className="table-option">
                        <img className="draggable-icon" src="/assets/icons/draggable-icon.svg"/>
                        <TableImg src="/assets/icons/tables/wide-rectangle-table.svg"/>
                    </div>
                    <div className="table-option">
                        <img className="draggable-icon" src="/assets/icons/draggable-icon.svg"/>
                        <TableImg src="/assets/icons/tables/long-rectangle-table.svg"/>
                    </div>
                </div>

                <div className="saved-presets">

                </div>

                <div className="preset-buttons">
                    <button>Save Preset</button>
                    <button>Delete Preset</button>
                </div>
            </div>
        </div>

    )
}
