import React, { useState, useRef, useEffect } from "react";
import styled from "styled-components";
import Draggable from "react-draggable";

const TableImg = styled.img`
  width: 60%;
  height: 60%;
  pointer-events: none; /* This ensures the image doesn't interfere with dragging */
`;

export default function DraggableTable({
  img,
  gridRef,
  addElementToGrid,
  updatePosition,
  initialPosition = { x: 0, y: 0 },
  isInGrid = false,
  resetOnDrop = false,
  isDraggable = true, // New prop to control draggable state
  isSelected,
  onSelect
}) {
  const [dragging, setDragging] = useState(false);
  const [position, setPosition] = useState(initialPosition);
  const draggableRef = useRef(null);

  useEffect(() => {
    if (!isDraggable) {
      setDragging(false);
    }
  }, [isDraggable]);

  const handleDragStop = (e, data) => {
    const gridRect = gridRef.current.getBoundingClientRect();
    const draggableRect = draggableRef.current.getBoundingClientRect();

    if (
      draggableRect.left >= gridRect.left &&
      draggableRect.right <= gridRect.right &&
      draggableRect.top >= gridRect.top &&
      draggableRect.bottom <= gridRect.bottom
    ) {
      // Element is within the grid bounds
      const newX = draggableRect.left - gridRect.left;
      const newY = draggableRect.top - gridRect.top;

      if (isInGrid) {
        setPosition({ x: newX, y: newY });
        if (updatePosition) {
          updatePosition({ x: newX, y: newY });
        }
      } else {
        addElementToGrid(
          <DraggableTable
            img={img}
            gridRef={gridRef}
            addElementToGrid={addElementToGrid}
            updatePosition={updatePosition}
            initialPosition={{ x: newX, y: newY }}
            isInGrid={true}
            isDraggable={isDraggable} // Pass the prop
            isSelected={isSelected}
            onSelect={onSelect}
          />,
          { x: newX, y: newY }
        );
        if (resetOnDrop) {
          setPosition({ x: 0, y: 0 });
        }
      }
    } else {
      // Element is outside the grid bounds, reset position
      setPosition(initialPosition);
    }

    setDragging(false);
  };

  return (
    <Draggable
      nodeRef={draggableRef}
      onStart={() => isDraggable && setDragging(true)}
      onStop={isDraggable ? handleDragStop : null}
      position={position}
      disabled={!isDraggable} // Disable dragging if not in edit mode
    >
      <div
        ref={draggableRef}
        className={`draggable-content ${dragging ? "dragging" : ""}`}
        style={{
          opacity: !isDraggable && isSelected ? 1 : !isDraggable && !isSelected ? 0.5 : 1,
          transform: isSelected && !isDraggable ? "translateY(-5px)" : "translateY(0)",
          transition: "transform 0.2s",
        }}
        onClick={() => !isDraggable && onSelect()} // Only selectable when not draggable
      >
        <img className="draggable-icon" src="/assets/icons/draggable-icon.svg" alt="draggable icon" />
        <TableImg src={img} />
        {isSelected && !isDraggable && (
          <div className="table-menu">
            <p>#20</p>
            <p>served by Mahad R.</p>
            <button>+ serve</button>
          </div>
        )}
      </div>

    </Draggable>
  );
}
