import React, { useState } from "react";
import styled from "styled-components";
import Draggable from "react-draggable";

import Icon from "../components/Icon"; // Ensure this component is correctly imported or replace it with the correct import

export default function TableManagement() {
    const [zoomLevel, setZoomLevel] = useState(1);
    const [dragging, setDragging] = useState(false);

    const Grid = styled.div`
        background-size: ${40 + 1 * zoomLevel}px ${40 + 1 * zoomLevel}px;
    `;

    const TableImg = styled.img`
        width: 60%;
        height: 60%;
    `;

    const handleZoomLevel = (value) => {
        console.log(zoomLevel);
        setZoomLevel(zoomLevel + value);
    };

    return (
        <div className="table-management">
            <div className="table-grid-container">
                <div className="table-zoom-settings">
                    <button onClick={() => handleZoomLevel(10)}>+</button>
                    <button onClick={() => handleZoomLevel(-10)}>-</button>
                    <button onClick={() => setZoomLevel(1)}>100%</button>
                </div>
                <Grid className="table-grid"></Grid>
            </div>
            <div className="table-presets">
                <h3 className="edit-tables-title">Edit Tables</h3>
                <div className="table-options">
                    <div className="table-option">
                    <Draggable
                        onStart={() => setDragging(true)}
                        onStop={() => setDragging(false)}
                    >

                            <img className="draggable-icon" src="/assets/icons/draggable-icon.svg" alt="draggable icon" />
                            <TableImg src="/assets/icons/tables/round-table.svg" alt="round table" />

                    </Draggable>
                    </div>
                    <div className="table-option">
                        <img className="draggable-icon" src="/assets/icons/draggable-icon.svg" alt="draggable icon" />
                        <TableImg src="/assets/icons/tables/square-table.svg" alt="square table" />
                    </div>
                    <div className="table-option">
                        <img className="draggable-icon" src="/assets/icons/draggable-icon.svg" alt="draggable icon" />
                        <TableImg src="/assets/icons/tables/wide-rectangle-table.svg" alt="wide rectangle table" />
                    </div>
                    <div className="table-option">
                        <img className="draggable-icon" src="/assets/icons/draggable-icon.svg" alt="draggable icon" />
                        <TableImg src="/assets/icons/tables/long-rectangle-table.svg" alt="long rectangle table" />
                    </div>
                </div>
                <div className="saved-presets"></div>
                <div className="preset-buttons">
                    <button>Save Preset</button>
                    <button>Delete Preset</button>
                </div>
            </div>
        </div>
    );
}

