import './App.scss';
import Header from './components/Header';
import Nav from './components/Nav';
import Icon from './components/Icon';
import TableManagement from './components/TableManagement';
import Menu from './components/Menu';
import { useState, useRef } from 'react';

import './style/main.scss';

function App() {
  const [activeTab, setActiveTab] = useState('tables');

  // Refs to hold the components
  const tableManagementRef = useRef(null);
  const reservationsRef = useRef(null);
  const menuRef = useRef(null);

  return (
    <div className="App">
      <Nav />
      <section>
        <Header 
          title="Mahad's Cuisine & Bar"
        />
        <div className="tabs">
          <button 
            className={`tab-button ${activeTab === 'tables' ? 'active' : ''}`} 
            onClick={() => setActiveTab('tables')}
          >
            <Icon 
              img="/assets/icons/tables/round-table.svg"
              width="2rem"
              padding="0"
            />
            Tables
          </button>
          <button 
            className={`tab-button ${activeTab === 'reservations' ? 'active' : ''}`} 
            onClick={() => setActiveTab('reservations')}
          >
            <Icon 
              img="/assets/icons/reservation-icon.svg"
              width="2rem"
              padding="0"
            />
            Reservations
          </button>
          <button 
            className={`tab-button ${activeTab === 'menu' ? 'active' : ''}`} 
            onClick={() => setActiveTab('menu')}
          >
            <Icon 
              img="/assets/icons/hotdish-icon-blue.svg"
              width="2rem"
              padding="0"
            />
            Menu
          </button>
        </div>

        <div style={{ display: activeTab === 'tables' ? 'block' : 'none' }}>
          {tableManagementRef.current || <TableManagement ref={tableManagementRef} />}
        </div>
        <div style={{ display: activeTab === 'reservations' ? 'block' : 'none' }}>
          {reservationsRef.current || <div ref={reservationsRef}>Reservations Content</div>}
        </div>
        <div style={{ display: activeTab === 'menu' ? 'block' : 'none' }}>
          {menuRef.current || <Menu ref={menuRef} />}
        </div>
      </section>
    </div>
  );
}

export default App;
