import React, { useState, useRef } from "react";
import styled from "styled-components";
import Draggable from "react-draggable";

const TableImg = styled.img`
  width: 60%;
  height: 60%;
  pointer-events: none; /* This ensures the image doesn't interfere with dragging */
`;

export default function DraggableTable({ img, gridRef }) {
  const [dragging, setDragging] = useState(false);
  const [position, setPosition] = useState({ x: 0, y: 0 });
  const draggableRef = useRef(null);

  const handleDragStop = (e, data) => {
    const gridRect = gridRef.current.getBoundingClientRect();
    const draggableRect = draggableRef.current.getBoundingClientRect();

    if (
      draggableRect.x >= gridRect.x &&
      draggableRect.x + draggableRect.width <= gridRect.x + gridRect.width &&
      draggableRect.y >= gridRect.y &&
      draggableRect.y + draggableRect.height <= gridRect.y + gridRect.height
    ) {
      // Element is within the grid bounds
      const newX = draggableRect.x - gridRect.x;
      const newY = draggableRect.y - gridRect.y;
      setPosition({ x: newX, y: newY });
    } else {
      // Element is outside the grid bounds, reset position
      setPosition({ x: 0, y: 0 });
      console.log("out of bounds")
    }

    setDragging(false);
  };

  return (
    <div className="table-option" ref={draggableRef} style={{ position: "relative" }}>
      <Draggable
        onStart={() => setDragging(true)}
        onStop={handleDragStop}
        position={position}
      >
        <div className={`draggable-content ${dragging ? "dragging" : ""}`}>
          <img className="draggable-icon" src="/assets/icons/draggable-icon.svg" alt="draggable icon" />
          <TableImg src={img} />
        </div>
      </Draggable>
    </div>
  );
}

// Add this CSS to your stylesheet or a styled component
const styles = `
.table-option {
  display: flex;
  justify-content: center;
  align-items: center;
  width: 10vw;
  min-width: 9rem;
  min-height: 10rem;
  border: 1px solid #38C6F450;
  position: relative;
  cursor: pointer;
  transition: all 0.2s ease;
}

.draggable-content.dragging {
  position: absolute !important;
  z-index: 1000;
  transition: none;
}
`;

// Inject the styles into the document head
const styleSheet = document.createElement("style");
styleSheet.type = "text/css";
styleSheet.innerText = styles;
document.head.appendChild(styleSheet);
