import React, { useState } from 'react';
import styled from 'styled-components';

const MenuContainer = styled.div`
  padding: 2rem;
`;

const SearchBar = styled.input`
  width: 100%;
  padding: 1rem 0.5rem;
  margin-bottom: 1rem;
  border: none;
  border-bottom: 1px solid #ccc;
  outline: none;

  &:focus {
    border-bottom: 1px solid #003366; /* dark blue color */
  }
`;

const MenuOptions = styled.div`
  display: flex;
  justify-content: space-between;
  margin-bottom: 1rem;
`;

const MenuButton = styled.button`
  padding: 0.5rem 1rem;
  border: 1px solid #0ACDFF;
  background-color: white;
  color: #0ACDFF;
  border-radius: 0.25rem;
  cursor: pointer;

  &:hover {
    background-color: #0ACDFF;
    color: white;
  }
`;

const MenuList = styled.div`
  display: flex;
  flex-wrap: wrap;
`;

const MenuItem = styled.div`
  width: 200px;
  margin: 1rem;
  padding: 1rem;
  border: 1px solid #ccc;
  border-radius: 0.25rem;
  text-align: center;

  img {
    max-width: 100%;
    height: auto;
    margin-bottom: 1rem;
  }
`;

const FormContainer = styled.div`
  display: flex;
  flex-direction: column;
  width: 100%;
  margin: 0 auto;
`;

const InputField = styled.input`
  padding: 1rem 0.5rem;
  margin: 1rem 0;
  border: none;
  border-bottom: 1px solid #004E7A;
  outline: none;
  width: 100%;
  color: #004E7A; 
  font-size: 1.5rem;

  &:focus {
    border-bottom: 1px solid #004E7A; /* dark blue color */
  }
`;

const TextArea = styled.textarea`
  padding: 1rem 0.5rem;
  margin: 1rem 0;
  border: none;
  border-bottom: 1px solid #ccc;
  outline: none;
  width: 100%;

  &:focus {
    border-bottom: 1px solid #003366; /* dark blue color */
  }
`;

const Label = styled.label`
  color: #808080; /* main grey color */
  margin-bottom: 0.5rem;
`;

const ImageUpload = styled.div`
  padding: 1rem;
  margin: 1rem 0;
  border: 1px dashed #ccc;
  border-radius: 0.25rem;
  text-align: center;

  input {
    display: none;
  }

  label {
    cursor: pointer;
    color: #0ACDFF;
    text-decoration: underline;
  }

  img {
    max-width: 100%;
    height: auto;
    margin-top: 1rem;
  }
`;

const ButtonContainer = styled.div`
  display: flex;
  justify-content: flex-end;
  margin-top: 1rem;
`;

const OptionsContainer = styled.div`
  display: flex;
  flex-direction: column;
  margin-left: 2rem;
`;

const OptionList = styled.div`
  display: flex;
  flex-wrap: wrap;
  margin-bottom: 1rem;

  .option {
    display: flex;
    align-items: center;
    margin: 0.5rem;
    padding: 0.5rem;
    border: 1px solid #ccc;
    border-radius: 0.25rem;
    background-color: #f9f9f9;
  }

  .remove-button {
    margin-left: 0.5rem;
    cursor: pointer;
  }
`;

const OptionInputContainer = styled.div`
  display: flex;
  align-items: center;

  input {
    margin-right: 0.5rem;
    padding: 0.5rem;
    border: none;
    border-bottom: 1px solid #ccc;
    outline: none;

    &:focus {
      border-bottom: 1px solid #003366; /* dark blue color */
    }
  }

  .add-option-button {
    padding: 0.5rem 1rem;
    border: 1px solid #0ACDFF;
    background-color: white;
    color: #0ACDFF;
    border-radius: 0.25rem;
    cursor: pointer;

    &:hover {
      background-color: #0ACDFF;
      color: white;
    }
  }
`;

const Menu = () => {
  const [menuItems, setMenuItems] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [isCreatingItem, setIsCreatingItem] = useState(false);
  const [newItem, setNewItem] = useState({ name: '', price: '', description: '', image: '', options: [] });
  const [optionName, setOptionName] = useState('');
  const [optionPrice, setOptionPrice] = useState('');

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewItem({ ...newItem, [name]: value });
  };

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    const reader = new FileReader();
    reader.onloadend = () => {
      setNewItem({ ...newItem, image: reader.result });
    };
    if (file) {
      reader.readAsDataURL(file);
    }
  };

  const handleSaveItem = () => {
    setMenuItems([...menuItems, newItem]);
    setNewItem({ name: '', price: '', description: '', image: '', options: [] });
    setIsCreatingItem(false);
  };

  const handleAddOption = () => {
    const newOption = { name: optionName, price: optionPrice };
    setNewItem({ ...newItem, options: [...newItem.options, newOption] });
    setOptionName('');
    setOptionPrice('');
  };

  const handleRemoveOption = (index) => {
    const updatedOptions = newItem.options.filter((_, i) => i !== index);
    setNewItem({ ...newItem, options: updatedOptions });
  };

  const filteredMenuItems = menuItems.filter(item =>
    item.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <MenuContainer>
      {!isCreatingItem ? (
        <>
          <MenuOptions>
            <SearchBar
              type="text"
              placeholder="Search"
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
            <div>
              <MenuButton onClick={() => setIsCreatingItem(true)}>Create Menu Item</MenuButton>
            </div>
          </MenuOptions>
          <MenuList>
            {filteredMenuItems.length > 0 ? (
              filteredMenuItems.map((item, index) => (
                <MenuItem key={index}>
                  {item.image && <img src={item.image} alt={item.name} />}
                  <h4>{item.name}</h4>
                  <p>{item.price}</p>
                </MenuItem>
              ))
            ) : (
              <p>+ Add menu items</p>
            )}
          </MenuList>
        </>
      ) : (
        <FormContainer>
          <h2 style={{ fontSize: '2rem', color: '#004E7A'}}>Create Menu Item</h2>
          <div style={{ display: 'flex', gap: '1rem', width: '100%', border: '1px solid green' }}>
            <div style={{ width: '50%'}}>
              <Label>Name</Label>
              <InputField
                type="text"
                name="name"
                value={newItem.name}
                onChange={handleInputChange}
              />
              <Label>Price</Label>
              <InputField
                type="text"
                name="price"
                value={newItem.price}
                onChange={handleInputChange}
              />
              <Label>Description</Label>
              <TextArea
                name="description"
                value={newItem.description}
                onChange={handleInputChange}
              />
              <Label>Image</Label>
              <ImageUpload>
                <input
                  type="file"
                  id="file"
                  onChange={handleImageUpload}
                />
                <label htmlFor="file">Drag and drop to upload file or Browse</label>
                {newItem.image && <img src={newItem.image} alt="preview" />}
              </ImageUpload>
            </div>
            <OptionsContainer>
              <Label>Options</Label>
              <OptionList>
                {newItem.options.map((option, index) => (
                  <div className="option" key={index}>
                    <span>{option.name} +${option.price}</span>
                    <span
                      className="remove-button"
                      onClick={() => handleRemoveOption(index)}
                    >
                      x
                    </span>
                  </div>
                ))}
              </OptionList>
              <OptionInputContainer>
                <InputField
                  type="text"
                  placeholder="Name"
                  value={optionName}
                  onChange={(e) => setOptionName(e.target.value)}
                />
                <InputField
                  type="text"
                  placeholder="Price"
                  value={optionPrice}
                  onChange={(e) => setOptionPrice(e.target.value)}
                />
                <button
                  className="add-option-button"
                  onClick={handleAddOption}
                >
                  ✓
                </button>
              </OptionInputContainer>
            </OptionsContainer>
          </div>
          <ButtonContainer>
            <MenuButton onClick={() => setIsCreatingItem(false)}>Close</MenuButton>
            <MenuButton onClick={handleSaveItem}>Save</MenuButton>
          </ButtonContainer>
        </FormContainer>
      )}
    </MenuContainer>
  );
};

export default Menu;
