import React, { useState } from 'react';
import { register } from '../services/api';
import { Link, useNavigate } from 'react-router-dom';
import '../style/register.scss';

const Register = () => {
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: ''
  });
  const [businessData, setBusinessData] = useState({
    restaurantName: '',
    ownerName: '',
    profilePicture: null
  });
  const [isRegistered, setIsRegistered] = useState(false);
  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    if (files) {
      setBusinessData({ ...businessData, [name]: files[0] });
    } else if (isRegistered) {
      setBusinessData({ ...businessData, [name]: value });
    } else {
      setFormData({ ...formData, [name]: value });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!isRegistered) {
      if (formData.username && formData.email && formData.password) {
        try {
          const response = await register(formData);
          setMessage('Successfully registered!');
          setIsRegistered(true);
          localStorage.setItem('user', JSON.stringify(response.data.user)); // Save user data to localStorage
        } catch (error) {
          setMessage('Registration error');
          console.error('Registration error:', error);
        }
      } else {
        setMessage('Please fill out all fields');
      }
    } else {
      if (businessData.restaurantName && businessData.ownerName && businessData.profilePicture) {
        // Simulate saving business details
        setMessage('Business details saved successfully!');
        setTimeout(() => navigate('/'), 2000); // Redirect to the main page after 2 seconds
      } else {
        setMessage('Please fill out all fields');
      }
    }
  };

  return (
    <div className="register-container">
      <h1>FloatPOS</h1>
      <img src="/placeholder-logo.png" alt="Logo" className="logo"/>
      {!isRegistered ? (
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            name="username"
            placeholder="Username"
            value={formData.username}
            onChange={handleChange}
          />
          <input
            type="email"
            name="email"
            placeholder="Email"
            value={formData.email}
            onChange={handleChange}
          />
          <input
            type="password"
            name="password"
            placeholder="Password"
            value={formData.password}
            onChange={handleChange}
          />
          <button type="submit">Register</button>
          {message && <p>{message}</p>}
          <p>Already have an account? <Link to="/login">Login</Link></p>
        </form>
      ) : (
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            name="restaurantName"
            placeholder="Restaurant Name"
            value={businessData.restaurantName}
            onChange={handleChange}
          />
          <input
            type="text"
            name="ownerName"
            placeholder="Owner Name"
            value={businessData.ownerName}
            onChange={handleChange}
          />
          <input
            type="file"
            name="profilePicture"
            accept="image/*"
            onChange={handleChange}
          />
          <button type="submit">Done</button>
          {message && <p>{message}</p>}
        </form>
      )}
    </div>
  );
};

export default Register;
