import React, { useState } from 'react';
import './style/Menu.scss';
import Receipt from './Receipt'; // Import the new Receipt component

const Menu = () => {
  const [menuItems, setMenuItems] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [isCreatingItem, setIsCreatingItem] = useState(false);
  const [newItem, setNewItem] = useState({ name: '', price: '', description: '', image: '', options: [] });
  const [optionName, setOptionName] = useState('');
  const [optionPrice, setOptionPrice] = useState('');
  const [selectedItem, setSelectedItem] = useState(null);
  const [receiptItems, setReceiptItems] = useState([]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewItem({ ...newItem, [name]: value });
  };

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    const reader = new FileReader();
    reader.onloadend = () => {
      setNewItem({ ...newItem, image: reader.result });
    };
    if (file) {
      reader.readAsDataURL(file);
    }
  };

  const handleSaveItem = () => {
    setMenuItems([...menuItems, newItem]);
    setNewItem({ name: '', price: '', description: '', image: '', options: [] });
    setIsCreatingItem(false);
  };

  const handleAddOption = () => {
    const newOption = { name: optionName, price: optionPrice };
    setNewItem({ ...newItem, options: [...newItem.options, newOption] });
    setOptionName('');
    setOptionPrice('');
  };

  const handleRemoveOption = (index) => {
    const updatedOptions = newItem.options.filter((_, i) => i !== index);
    setNewItem({ ...newItem, options: updatedOptions });
  };

  const filteredMenuItems = menuItems.filter(item =>
    item.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const handleItemClick = (item) => {
    setSelectedItem(item);
  };

  const handleClosePanel = () => {
    setSelectedItem(null);
  };

  const calculateTotal = (price, options) => {
    const subtotal = options.reduce((acc, option) => acc + parseFloat(option.price || 0), parseFloat(price));
    const tax = subtotal * 0.13;
    const total = subtotal + tax;
    return { subtotal, tax, total };
  };

  const handleAddToTakeout = (item) => {
    setReceiptItems([...receiptItems, item]);
    setSelectedItem(null);
  };

  const handleAddMenuItem = () => {
    // Logic to switch to the menu tab and allow adding a new menu item
  };

  return (
    <div className="menu-container">
      {!isCreatingItem ? (
        <>
          <div className="menu-options">
            <input
              className="search-bar"
              type="text"
              placeholder="Search"
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
            <div>
              <button className="menu-button" onClick={() => setIsCreatingItem(true)}>Create Menu Item</button>
            </div>
          </div>
          <div className="menu-list">
            {filteredMenuItems.length > 0 ? (
              filteredMenuItems.map((item, index) => (
                <div className="menu-item" key={index} onClick={() => handleItemClick(item)}>
                  {item.image && <img src={item.image} alt={item.name} />}
                  <h4>{item.name}</h4>
                  <p>{item.price}</p>
                </div>
              ))
            ) : (
              <p>+ Add menu items</p>
            )}
          </div>
        </>
      ) : (
        <div className="form-container">
          <h2>Create Menu Item</h2>
          <label className="label">Name</label>
          <input
            className="input-field"
            type="text"
            name="name"
            value={newItem.name}
            onChange={handleInputChange}
          />
          <label className="label">Price</label>
          <input
            className="input-field"
            type="text"
            name="price"
            value={newItem.price}
            onChange={handleInputChange}
          />
          <label className="label">Description</label>
          <textarea
            className="text-area"
            name="description"
            value={newItem.description}
            onChange={handleInputChange}
          />
          <label className="label">Image</label>
          <div className="image-upload">
            {newItem.image ? (
              <img src={newItem.image} alt="Menu item" />
            ) : (
              <>
                <input
                  type="file"
                  id="imageUpload"
                  onChange={handleImageUpload}
                />
                <label htmlFor="imageUpload">Drag and drop to upload file or <span>Browse</span></label>
              </>
            )}
          </div>
          <label className="label">Options</label>
          <div className="options-container">
            <div className="option-list">
              {newItem.options.map((option, index) => (
                <div key={index} className="option">
                  {option.name} +${option.price}
                  <button className="remove-button" onClick={() => handleRemoveOption(index)}>x</button>
                </div>
              ))}
            </div>
            <div className="option-input-container">
              <input
                className="input-field"
                type="text"
                placeholder="Name"
                value={optionName}
                onChange={(e) => setOptionName(e.target.value)}
              />
              <input
                className="input-field"
                type="text"
                placeholder="Price"
                value={optionPrice}
                onChange={(e) => setOptionPrice(e.target.value)}
              />
              <button
                className="add-option-button"
                onClick={handleAddOption}
              >
                ✓
              </button>
            </div>
          </div>
          <div className="button-container">
            <button className="menu-button" onClick={() => setIsCreatingItem(false)}>Close</button>
            <button className="menu-button" onClick={handleSaveItem}>Save</button>
          </div>
        </div>
      )}

      <div className={`slide-out-panel ${selectedItem ? 'open' : ''}`}>
        {selectedItem && (
          <>
            <button className="close-button" onClick={handleClosePanel}>×</button>
            <h2 className="slide-out-title">{selectedItem.name}</h2>
            {selectedItem.image && <img className="slide-out-image" src={selectedItem.image} alt={selectedItem.name} />}
            <p className="slide-out-price"><strong>Price:</strong> ${selectedItem.price}</p>
            <p className="slide-out-description"><strong>Description:</strong> {selectedItem.description}</p>
            <p className="slide-out-options"><strong>Options:</strong></p>
            <ul>
              {selectedItem.options.map((option, index) => (
                <li key={index} className="menu-item-option">{option.name} +${option.price}</li>
              ))}
            </ul>
            <div className="slide-out-total">
              <p><strong>Subtotal:</strong> ${calculateTotal(selectedItem.price, selectedItem.options).subtotal.toFixed(2)}</p>
              <p><strong>Tax:</strong> ${calculateTotal(selectedItem.price, selectedItem.options).tax.toFixed(2)}</p>
              <p><strong>Total:</strong> ${calculateTotal(selectedItem.price, selectedItem.options).total.toFixed(2)}</p>
              <button className="add-button" onClick={() => handleAddToTakeout(selectedItem)}>+ Add to Table</button>
              <button className="add-button" onClick={() => handleAddToTakeout(selectedItem)}>+ Add to Takeout</button>
            </div>
          </>
        )}
      </div>

      {receiptItems.map((item, index) => (
        <Receipt key={index} menuItem={item} onAddMenuItem={handleAddMenuItem} />
      ))}
    </div>
  );
};

export default Menu;
