import React, { useState } from 'react';
import '../style/receipt.scss';
import Draggable from 'react-draggable';

const Receipt = ({ receiptItems, onAddMenuItem }) => {
  const [total, setTotal] = useState(0);

  const calculateTotal = () => {
    let newTotal = receiptItems.reduce((acc, item) => acc + item.price, 0);
    setTotal(newTotal);
  };

  return (
    <div className="receipt-container">
      <div className="receipt-header">
        <h2>Receipt for table A20</h2>
        <div className="receipt-meta">
          <p>served by</p>
          <input type="text" placeholder="Server Name" />
          <p>{new Date().toLocaleString()}</p>
        </div>
      </div>
      <table className="receipt-table">
        <thead>
          <tr>
            <th>QTY</th>
            <th>ITEM</th>
            <th>PRICE</th>
          </tr>
        </thead>
        <tbody>
          {receiptItems.map((item, index) => (
            <tr key={index}>
              <td>(1)</td>
              <td>{item.name}</td>
              <td>${item.price.toFixed(2)}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <button onClick={onAddMenuItem} className="add-menu-item-button">+ Add menu item</button>
      <div className="receipt-total">
        <p>TOTAL: ${total.toFixed(2)}</p>
      </div>
      <div className="receipt-actions">
        <button className="receipt-button">discount</button>
        <button className="receipt-button">split</button>
        <button className="receipt-button">Pay ($)</button>
      </div>
    </div>
  );
};

export default Receipt;
